import React, { useState } from "react";
import Modal from "react-modal";
import moment from "moment";

Modal.setAppElement("#root");

const EventModal = ({ selectedDate, setModalOpen, addEvent }) => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    participants: "",
    date: moment(selectedDate).format("YYYY-MM-DD"),
    time: "",
    duration: 1,
    notes: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.title || !formData.date || !formData.time) {
      alert("Please fill in all required fields.");
      return;
    }

    addEvent({
      title: formData.title,
      start: new Date(`${formData.date}T${formData.time}`),
      end: new Date(
        moment(`${formData.date}T${formData.time}`).add(formData.duration, "hours")
      ),
    });

    setModalOpen(false);
  };

  return (
    <Modal isOpen={true} onRequestClose={() => setModalOpen(false)}>
      <h2>Create Event</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Event Title" onChange={handleChange} required />
        <textarea name="description" placeholder="Description" onChange={handleChange}></textarea>
        <input type="text" name="participants" placeholder="Participants (comma-separated)" onChange={handleChange} />
        <input type="date" name="date" onChange={handleChange} required />
        <input type="time" name="time" onChange={handleChange} required />
        <input type="number" name="duration" placeholder="Duration (hrs)" onChange={handleChange} required />
        <textarea name="notes" placeholder="Session Notes" onChange={handleChange}></textarea>
        <button type="submit">Create Event</button>
      </form>
    </Modal>
  );
};

export default EventModal;
