import React, { useState } from "react";
import Modal from "react-modal";
import moment from "moment";
import axios from "axios";

Modal.setAppElement("#root");

const EventModal = ({ selectedDate, setModalOpen, addEvent }) => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    participants: "",
    date: moment(selectedDate).format("YYYY-MM-DD"),
    time: "",
    duration: 1,
    notes: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.title || !formData.date || !formData.time) {
      alert("Please fill in all required fields.");
      return;
    }

    const startDateTime = `${formData.date}T${formData.time}`;
    const endDateTime = moment(startDateTime)
      .add(formData.duration, "hours")
      .format();

    const eventData = {
      title: formData.title,
      start: new Date(startDateTime),
      end: new Date(endDateTime),
      description: formData.description,
      participants: formData.participants,
    };

    addEvent(eventData);

    try {
      await axios.post("http://localhost:5000/add-event", eventData);
      alert("Event added successfully!");
    } catch (error) {
      console.error(error);
      alert("Error adding event");
    }

    setModalOpen(false);
  };

  return (
    <Modal isOpen={true} onRequestClose={() => setModalOpen(false)}>
      <h2>Create Event</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Event Title" onChange={handleChange} required />
        <textarea name="description" placeholder="Description" onChange={handleChange}></textarea>
        <input type="text" name="participants" placeholder="Participants (comma-separated)" onChange={handleChange} />
        <input type="date" name="date" onChange={handleChange} required />
        <input type="time" name="time" onChange={handleChange} required />
        <input type="number" name="duration" placeholder="Duration (hrs)" onChange={handleChange} required />
        <textarea name="notes" placeholder="Session Notes" onChange={handleChange}></textarea>
        <button type="submit">Create Event</button>
      </form>
    </Modal>
  );
};

export default EventModal;
