import React, { useState } from "react";

const EventModal = ({ isOpen, onClose, onSubmit }) => {
    const [eventData, setEventData] = useState({
        title: "",
        description: "",
        participants: "",
        date: "",
        time: "",
        duration: "",
        sessionNotes: "",
      });
  if (!isOpen) return null;



  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(eventData);
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>Create Event</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="title" placeholder="Event Title" required onChange={handleChange} />
          <input type="text" name="description" placeholder="Description" onChange={handleChange} />
          <input type="text" name="participants" placeholder="Participants (comma-separated emails)" onChange={handleChange} />
          <input type="date" name="date" required onChange={handleChange} />
          <input type="time" name="time" required onChange={handleChange} />
          <input type="number" name="duration" placeholder="Duration (hrs)" required onChange={handleChange} />
          <textarea name="sessionNotes" placeholder="Session Notes" onChange={handleChange}></textarea>
          <button type="submit">Create Event</button>
          <button onClick={onClose}>Cancel</button>
        </form>
      </div>
    </div>
  );
};

export default EventModal;
